package com.norelax.www.entity;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;/** * person类 * * @author wusong * @create 2017-06-15 下午4:55 **/@Configurationpublic class Person {    private String name;    private String address;    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getAddress() {        return address;    }    public void setAddress(String address) {        this.address = address;    }    @Override    public String toString() {        return "Person{" +                "name='" + name + '\'' +                ", address='" + address + '\'' +                '}';    }    @Bean    public Person person1(@Value("${name}")String name,@Value("${address}")String address) {        Person person = new Person();        person.setName(name);        person.setAddress(address);        return person;    }}